import React, { useState } from 'react'

const TableData = () => {

    const data =
        [
            {
                "country": "Afghanistan",
                "states": ["Badakhshan", "Badghis", "Baghlan", "Balkh", "Bamian", "Daykondi", "Farah", "Faryab", "Ghazni", "Ghowr"]
            },
            {
                "country": "Bangladesh",
                "states": ["Barisal", "Chittagong", "Dhaka", "Khulna", "Rajshahi", "Sylhet"]
            },
            {
                "country": "Brazil",
                "states": ["Acre", "Alagoas", "Amapa", "Amazonas", "Bahia", "Ceara", "Distrito Federal", "Espirito Santo", "Goias", "Maranhao"]
            },
            {
                "country": "Canada",
                "states": ["Alberta", "British Columbia", "Manitoba", "New Brunswick", "Newfoundland and Labrador", "Northwest Territories", "Nova Scotia", "Nunavut"]
            },
            {
                "country": "China",
                "states": ["Anhui", "Fujian", "Gansu", "Guangdong", "Guizhou", "Hainan", "Hebei", "Heilongjiang", "Henan", "Hubei"]
            },
            {
                "country": "India",
                "states": ["Assam", "Bihar", "Daman and Diu", "Delhi", "Goa", "Gujarat", "Haryana", "Karnataka", "Punjab", "Rajasthan"]
            },
            {
                "country": "Nepal",
                "states": ["Bagmati", "Bheri", "Dhawalagiri", "Gandaki", "Janakpur", "Karnali", "Kosi", "Lumbini", "Mahakali", "Mechi"]
            },
            {
                "country": "New Zealand",
                "states": ["Auckland", "Bay of Plenty", "Canterbury", "Chatham Islands", "Gisborne", "Hawke's Bay", "Manawatu-Wanganui", "Marlborough", "Nelson", "Northland"]
            },
            {
                "country": "Russia",
                "states": ["Amur", "Arkhangel'sk", "Astrakhan'", "Belgorod", "Bryansk", "Chelyabinsk", "Chita", "Irkutsk", "Ivanovo", "Kaliningrad"]
            },
            {
                "country": "Pakistan",
                "states": ["Balochistan", "North-West Frontier Province", "Punjab", "Sindh", "Islamabad Capital Territory", "Federally Administered Tribal Areas"]
            },
        ]
    const city = [{ stste: "Gujarat", city: ["surat", "bhavnagar", "amreli", "rajkot",] }, { state: "Rajasthan", city: [] }]
    const [stateData, setStateData] = useState("");


    return (
        <>
            <table>
                <thead>
                    <tr>
                        <th>Id</th> <th>
                            Country
                        </th>
                        <th>
                            State
                        </th>
                        <th>
                            City
                        </th></tr>
                </thead>
                <tbody>
                    {
                        data && data.map((item, i) => (<>
                            <tr>
                                <td>{ i + 1 }</td>
                                <td ><a onClick={ () => setStateData(item.country) } >{ item.country }</a></td>
                                <td>{ item.country == stateData ? item?.states : "" }</td>
                            </tr>
                        </>))
                    }
                </tbody>
            </table>
        </>
    )
}

export default TableData
