import React, { useEffect, useState } from 'react'

const Data = () => {

    const [data, setData] = useState([]);
    const [name, setName] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [userId, setuserId] = useState(null);

    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/users")
            .then(response => {
                return response.json()
            })
            .then(data => {
                setData(data)
                setName(data[0].name)
                setUsername(data[0].username)
                setEmail(data[0].email)
                setuserId(data[0].id)
            })
    }, []);

    function deleteUser(id) {
        fetch(`https://jsonplaceholder.typicode.com/users/${id}`, {
            method: "DELETE"
        }).then((response) => {
            response.json()
                .then((response) => {
                    console.log(response);
                })
        })
    }

    function seleteUser(id) {
        let item = data[id - 1];
        setName(item.name);
        setUsername(item.username);
        setEmail(item.email);
        setuserId(item.id);
    }

    return (
        <>
            {/* <div>
                { users.length > 0 && (
                    <ul>
                        { users.map(user => (
                            <li key={ user.id }>{ user.name }</li>
                        )) }
                    </ul>
                ) }
            </div> */}

            <table border="1">
                <tr>
                    <td>id</td>
                    <td>name</td>
                    <td>username</td>
                    <td>email</td>

                </tr>
                {
                    data.map((item) =>
                        <tr>
                            <td>{ item.id }</td>
                            <td>{ item.name }</td>
                            <td>{ item.username }</td>
                            <td>{ item.email }</td>
                            <td><button onClick={ () => deleteUser(item.id) }>update</button></td>
                            <td><button onClick={ () => seleteUser(item.id) }>delete</button></td>
                        </tr>
                    )
                }
            </table>
        </>
    )
}

export default Data
