import React, { useState } from 'react'
import "./Data.css";


const getlocalitems = () => {
    let list = localStorage.getItem('lists')
    // console.log('list::: ', list);

    if (list) {
        return JSON.parse(localStorage.getItem('lists'));
    } else {
        return [];
    }

}

const Data = () => {

    // const [data, setData] = useState([]);
    // const [name, setName] = useState("");
    // const [username, setUsername] = useState("");
    // const [email, setEmail] = useState("");
    // const [userId, setuserId] = useState(null);

    // useEffect(() => {
    //     fetch("https://jsonplaceholder.typicode.com/users")
    //         .then(response => {
    //             return response.json()
    //         })
    //         .then(data => {
    //             setData(data)
    //             setName(data[0].name)
    //             setUsername(data[0].username)
    //             setEmail(data[0].email)
    //             setuserId(data[0].id)
    //         })
    // }, []);

    // function deleteUser(id) {
    //     fetch(`https://jsonplaceholder.typicode.com/users/${id}`, {
    //         method: "DELETE"
    //     }).then((response) => {
    //         response.json()
    //             .then((response) => {
    //                 // console.log(response);
    //             })
    //     })
    // }

    // function seleteUser(id) {
    //     let item = data[id - 1];
    //     setName(item.name);
    //     setUsername(item.username);
    //     setEmail(item.email);
    //     setuserId(item.id);
    // }

    // function updateUser() {
    //     let item = { name, username, email }
    //     // // console.log('item::: ', item);
    //     fetch(`https://jsonplaceholder.typicode.com/users/${userId}`, {
    //         method: "PUT",
    //         headers: {
    //             'Accept': 'application/json',
    //             'Content-type': 'application/json',
    //         },
    //         body: JSON.stringify(item)
    //     }).then((response) => {
    //         response.json()
    //             .then((response) => {
    //                 // console.log(response);
    //             })
    //     })
    // }

    const [inputdata, setInputData] = useState("")
    const [items, setItems] = useState(getlocalitems());
    const [togglesubmit, setTogglesubmit] = useState(true);
    const [isEdititem, setIsEdititem] = useState(null);

    const addItem = () => {
        if (!inputdata) {
            alert("please fill the data")
        } else if (inputdata && !togglesubmit) {
            setItems(
                items.map((ele) => {
                    if (ele.id === isEdititem) {
                        return { ...ele, name: inputdata }
                    }
                    return ele;
                })
            )
            setTogglesubmit(true)
            setInputData("")
            setIsEdititem(null);
        }
        else {
            const allInputData = { id: new Date().getTime().toString(), name: inputdata }
            setItems([...items, allInputData])
            setInputData('')
        }
    }

    const deleteItem = (index) => {
        const updateditems = items.filter((ele) => {
            return index != ele.id;
        });
        setItems(updateditems)
    }

    const editItem = (id) => {
        let newEditItem = items.find((ele) => {
            return ele.id === id
        });
        // console.log('newEditItem::: ', newEditItem);

        setTogglesubmit(false)
        // console.log('setTogglesubmit::: ', togglesubmit);
        setInputData(newEditItem.name)
        setIsEdititem(id);

    }

    const updateData = () => {
        // setItems()
        // const oo = items.filter((ele) => ele.id == isEdititem)
        items && items.map((items) => {
            if (items.id == isEdititem) {
                items.name = inputdata;
            }
        });
        console.log('oo::: ', isEdititem);
        // items
    }

    return (

        <>
            <div>
                <div className="header">
                    <h2 style={ { margin: 5 } }>My To Do List</h2>
                    <input type="text" value={ inputdata }
                        onChange={ (e) => setInputData(e.target.value) } placeholder="Title..." />
                    {
                        togglesubmit ?
                            <span onClick={ addItem } className="addBtn">Add</span>
                            :
                            <span onClick={ updateData } className="addBtn">Edit</span>
                    }

                </div>
                <ul className='hello'>

                    {
                        items.map((ele) => {
                            return (
                                <ul key={ ele.id }>
                                    <li>{ ele.name }
                                        <span style={ { marginRight: "90px" } } onClick={ () => editItem(ele.id) } class="close">Edit</span>
                                        <span onClick={ () => deleteItem(ele.id) } class="close">Remove</span>
                                    </li>
                                </ul>
                            )
                        })
                    }

                </ul>
            </div>

        </>
    )
}

export default Data
